{% if site.releases %}
  {% assign feed_items = feed_items | concat: site.releases %}
{% endif %}
{% assign feed_items = feed_items | sort: 'date' | reverse %}
{% for item in feed_items limit:20 %}

    {{ item.title | xml_escape }}

    {{ item.url | absolute_url }}
    {% if item.date %}
      {{ item.date | date_to_xmlschema }}
    {% else %}
      {{ site.time | date_to_xmlschema }}
    {% endif %}
    {% assign summary = item.summary | default: item.description | default: item.excerpt %}
    {% if summary %}
      {{ summary | strip_html | strip_newlines | xml_escape }}
    {% endif %}
    {{ item.content }}

{% endfor %}
